function scoop { switch ($args[0]) {   'search' { return sfsu.exe search @($args | Select-Object -Skip 1) }   'list' { return sfsu.exe list @($args | Select-Object -Skip 1) }   'unused-buckets' { return sfsu.exe unused-buckets @($args | Select-Object -Skip 1) }   'describe' { return sfsu.exe describe @($args | Select-Object -Skip 1) }   'info' { return sfsu.exe info @($args | Select-Object -Skip 1) }   'outdated' { return sfsu.exe outdated @($args | Select-Object -Skip 1) }   'depends' { return sfsu.exe depends @($args | Select-Object -Skip 1) }   'status' { return sfsu.exe status @($args | Select-Object -Skip 1) }   'home' { return sfsu.exe home @($args | Select-Object -Skip 1) }   'cat' { return sfsu.exe cat @($args | Select-Object -Skip 1) }   'export' { return sfsu.exe export @($args | Select-Object -Skip 1) }   'checkup' { return sfsu.exe checkup @($args | Select-Object -Skip 1) } default { scoop.ps1 @args } } }
